import React,{useState} from "react";

function Form({albums, setAlbums}) {

  const newFormObj = {
    title:"",
    image:"",
    artist:"",
    song: "",
    genre: "",
    link:""
  }

  const [newForm, setNewForm] = useState(newFormObj)
  function handleChange (e) {
    setNewForm({
    ...newForm,
    [e.target.name]: e.target.value
    })};

function handleSubmit(e) {
e.preventDefault()
fetch("htttp://localhost:3000/albums", {
method: "POST",
headers: {
  "Content-Type": "application/JSON"
},
body: JSON.stringify(newForm)
})
e.target.reset()
setAlbums([...albums, newForm])
}

return (
<form onSubmit={handleSubmit} className="playlist-form">
  <input placeholder="Playlist Name" type="text" name="name" onChange={handleChange} />
  <input placeholder="New Image URL" type="text" name="image" onChange={handleChange}/>
  <input placeholder="Artist" type="text" name="aritist" onChange={handleChange} />
  <input placeholder="Song" type="text" name="song" onChange={handleChange} />
  <input placeholder="Genre" type="text" name="genre" onChange={handleChange} />\
  <input placeholder="YouTube-" type="text" name="name" onChange={handleChange} />

  <input type="submit" value="Create New Playlist" />
</form>
)} 



export default Form;
